Get-Volume |where Driveletter -ne $null |sort -Property Driveletter |select `@{L="LW"; E={$_.Driveletter}}, @{L="GesamtGB"; E={[math]::round(($_.size/1GB),2)}},@{L="FreiGB"; E={[math]::round($_.sizeremaining/1GB,2)}},@{L="Frei"; E={[math]::round($_.sizeremaining/$_.size,2)}} |Out-DefaultGet-Volume |where Driveletter -ne $null |sort -Property Driveletter |ft `@{L="LW"; E={$_.Driveletter};width=5}, @{Label="Gesamt GB"; Expression={"{0:N2}" -f ($_.size/1GB)}; width=10; align="right"},@{L="Frei GB"; E={"{0:N2}" -f ($_.sizeremaining/1GB)}; w=10; a="right"},@{L="Frei %"; E={"{0:P2}" -f ($_.sizeremaining/$_.size)};w=10; a="right"}